import React from 'react';
import { X, Folder, HardDrive, Clock, Settings, Info } from 'lucide-react';

const Sidebar = ({ isOpen, onClose, indexedFolders = [] }) => {
  if (!isOpen) return null;

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <>
      {/* Overlay */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 z-10 lg:hidden"
        onClick={onClose}
      />
      
      {/* Sidebar */}
      <div className={`
        fixed lg:relative inset-y-0 left-0 z-20 w-80 bg-white border-r border-gray-200 transform transition-transform duration-300 ease-in-out
        ${isOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
      `}>
        <div className="h-full flex flex-col">
          {/* Header */}
          <div className="p-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-semibold text-gray-900">Library</h2>
              <button
                onClick={onClose}
                className="p-2 rounded-md hover:bg-gray-100 transition-colors lg:hidden"
              >
                <X className="h-5 w-5 text-gray-600" />
              </button>
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto">
            {/* Indexed Folders Section */}
            <div className="p-4">
              <h3 className="text-sm font-medium text-gray-900 mb-3 flex items-center">
                <HardDrive className="h-4 w-4 mr-2" />
                Indexed Folders
              </h3>
              
              {indexedFolders.length === 0 ? (
                <div className="text-center py-8">
                  <Folder className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                  <p className="text-sm text-gray-500 mb-2">No folders indexed yet</p>
                  <p className="text-xs text-gray-400">
                    Add a folder to start searching
                  </p>
                </div>
              ) : (
                <div className="space-y-2">
                  {indexedFolders.map((folder, index) => (
                    <div
                      key={index}
                      className="p-3 rounded-lg border border-gray-200 hover:bg-gray-50 transition-colors"
                    >
                      <div className="flex items-start space-x-3">
                        <Folder className="h-5 w-5 text-blue-500 flex-shrink-0 mt-0.5" />
                        <div className="flex-1 min-w-0">
                          <p className="text-sm font-medium text-gray-900 truncate">
                            {folder.name || folder.path?.split('/').pop() || folder.path?.split('\\').pop()}
                          </p>
                          <p className="text-xs text-gray-500 truncate mt-1">
                            {folder.path}
                          </p>
                          {folder.lastIndexed && (
                            <div className="flex items-center mt-2 text-xs text-gray-400">
                              <Clock className="h-3 w-3 mr-1" />
                              Indexed {formatDate(folder.lastIndexed)}
                            </div>
                          )}
                          {folder.fileCount && (
                            <p className="text-xs text-gray-400 mt-1">
                              {folder.fileCount} files
                            </p>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Recent Searches Section */}
            <div className="p-4 border-t border-gray-200">
              <h3 className="text-sm font-medium text-gray-900 mb-3 flex items-center">
                <Clock className="h-4 w-4 mr-2" />
                Recent Searches
              </h3>
              
              <div className="text-center py-4">
                <p className="text-xs text-gray-400">
                  Recent searches will appear here
                </p>
              </div>
            </div>
          </div>

          {/* Footer */}
          <div className="p-4 border-t border-gray-200 bg-gray-50">
            <div className="space-y-2">
              <button className="w-full flex items-center space-x-2 px-3 py-2 text-sm text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-md transition-colors">
                <Settings className="h-4 w-4" />
                <span>Settings</span>
              </button>
              
              <button className="w-full flex items-center space-x-2 px-3 py-2 text-sm text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-md transition-colors">
                <Info className="h-4 w-4" />
                <span>About</span>
              </button>
            </div>
            
            <div className="mt-4 pt-4 border-t border-gray-200">
              <p className="text-xs text-gray-400 text-center">
                Semantic File Explorer v1.0
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
