import React from 'react';
import { ExternalLink, File, Download, Clock, MapPin } from 'lucide-react';

const PreviewPane = ({ result, isElectron }) => {
  const handleOpenFile = () => {
    if (result && isElectron && window.electronAPI) {
      window.electronAPI.openFile(result.path);
    } else if (result) {
      // Fallback for non-Electron environments
      console.log('Would open file:', result.path);
    }
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return 'Unknown';
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
  };

  if (!result) {
    return (
      <div className="h-full flex items-center justify-center bg-white border-l border-gray-200">
        <div className="text-center text-gray-500">
          <File className="h-12 w-12 mx-auto mb-4 text-gray-300" />
          <p className="text-lg mb-2">No file selected</p>
          <p className="text-sm">Click on a search result to preview it</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-white">
      {/* Header */}
      <div className="p-4 border-b border-gray-200 bg-gray-50">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-gray-900 truncate">
            {result.filename}
          </h2>
          <button
            onClick={handleOpenFile}
            className="flex items-center space-x-2 px-3 py-1.5 text-sm bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            title="Open file in default application"
          >
            <ExternalLink className="h-4 w-4" />
            <span>Open</span>
          </button>
        </div>
      </div>

      {/* File Details */}
      <div className="p-4 space-y-4">
        <div className="grid grid-cols-1 gap-4">
          {/* File Path */}
          <div className="flex items-start space-x-2">
            <MapPin className="h-4 w-4 text-gray-400 mt-0.5 flex-shrink-0" />
            <div>
              <p className="text-xs text-gray-500 uppercase font-medium">Location</p>
              <p className="text-sm text-gray-700 break-all">{result.path}</p>
            </div>
          </div>

          {/* File Size */}
          {result.size && (
            <div className="flex items-start space-x-2">
              <Download className="h-4 w-4 text-gray-400 mt-0.5 flex-shrink-0" />
              <div>
                <p className="text-xs text-gray-500 uppercase font-medium">Size</p>
                <p className="text-sm text-gray-700">{formatFileSize(result.size)}</p>
              </div>
            </div>
          )}

          {/* Modified Date */}
          {result.modified && (
            <div className="flex items-start space-x-2">
              <Clock className="h-4 w-4 text-gray-400 mt-0.5 flex-shrink-0" />
              <div>
                <p className="text-xs text-gray-500 uppercase font-medium">Modified</p>
                <p className="text-sm text-gray-700">
                  {new Date(result.modified).toLocaleString()}
                </p>
              </div>
            </div>
          )}

          {/* Relevance Score */}
          {result.score && (
            <div className="flex items-start space-x-2">
              <div className="h-4 w-4 mt-0.5 flex-shrink-0">
                <div className="h-full w-full bg-green-500 rounded-full"></div>
              </div>
              <div>
                <p className="text-xs text-gray-500 uppercase font-medium">Relevance</p>
                <p className="text-sm text-gray-700">
                  {Math.round(result.score * 100)}% match
                </p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Content Preview */}
      {result.snippet && (
        <div className="flex-1 p-4 border-t border-gray-200">
          <h3 className="text-sm font-medium text-gray-900 mb-3">Content Preview</h3>
          <div className="bg-gray-50 rounded-lg p-4 max-h-64 overflow-y-auto">
            <p className="text-sm text-gray-700 whitespace-pre-line leading-relaxed">
              {result.snippet}
            </p>
          </div>
        </div>
      )}

      {/* Full Content (for future implementation) */}
      {result.fullText && (
        <div className="flex-1 p-4 border-t border-gray-200 overflow-hidden">
          <h3 className="text-sm font-medium text-gray-900 mb-3">Full Content</h3>
          <div className="bg-gray-50 rounded-lg p-4 h-full overflow-y-auto">
            <p className="text-sm text-gray-700 whitespace-pre-line leading-relaxed">
              {result.fullText}
            </p>
          </div>
        </div>
      )}

      {/* Actions */}
      <div className="p-4 border-t border-gray-200 bg-gray-50">
        <div className="flex space-x-2">
          <button
            onClick={handleOpenFile}
            className="flex-1 flex items-center justify-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
          >
            <ExternalLink className="h-4 w-4" />
            <span>Open File</span>
          </button>
          
          <button
            onClick={() => navigator.clipboard.writeText(result.path)}
            className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition-colors"
            title="Copy file path"
          >
            Copy Path
          </button>
        </div>
      </div>
    </div>
  );
};

export default PreviewPane;
